set(loglog1(4),'LineStyle','-.','Color',[1 0 0],'DisplayName','LAS+LAS');

% Create xlabel
xlabel('File size in MSS','FontSize',16,'FontName','Times');

% Create ylabel
ylabel('Mean Response time (s)','FontSize',16,'FontName','Times');

% Create legend
legend1 = legend(axes1,'show');
